import React, { Component } from 'react';
import { FaCocktail, FaHiking, FaShuttleVab, FaBeer } from 'react-icons/fa';
import Title from './Title';

export default class Services extends Component {
	state = {
		services: [
			{
				icon: <FaCocktail />,
				title: 'Free cocktails',
				info:
					'Lorem ipsum dolor sit amet consectetur adipisicing elit. Enim magni cupiditate eligendi animi assumenda qui'
			},
			{
				icon: <FaHiking />,
				title: 'Endless Hiking',
				info:
					'Lorem ipsum dolor sit amet consectetur adipisicing elit. Enim magni cupiditate eligendi animi assumenda qui'
			},
			{
				icon: <FaShuttleVab />,
				title: 'Free Shuttle',
				info:
					'Lorem ipsum dolor sit amet consectetur adipisicing elit. Enim magni cupiditate eligendi animi assumenda qui'
			},
			{
				icon: <FaBeer />,
				title: 'Strongest Beer',
				info:
					'Lorem ipsum dolor sit amet consectetur adipisicing elit. Enim magni cupiditate eligendi animi assumenda qui'
			}
		]
	};
	render() {
		return (
			<section className='services'>
				<Title title='services' />
				<div className='services-center'>
					{this.state.services.map((item, index) => {
						return (
							<article key={index} className='service'>
								<spa>{item.title}</spa>
								<h6>{item.title}</h6>
								<p>{item.info}</p>
							</article>
						);
					})}
				</div>
			</section>
		);
	}
}
